import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { getDailyDetail } from "../api/getDaily";
import marked from "marked";
import { CategoryList } from "../components/CategoryList";
import axios from "axios";
import createHistory from "history/createBrowserHistory";

export const DailyDetail = (props) => {
  const initialState = {
    date: "",
    univ: "",
    study: "",
    other: "",
    first_meet: "",
    wanna_do: "",
    summary: "",
  };

  const history = createHistory();

  const [detail, setDetail] = useState(initialState);
  const [loading, setLoading] = useState(true);
  const { id } = useParams();

  useEffect(() => {
    getDailyDetail(id)
      .then((d) => {
        setDetail(d);
        setLoading(false);
      })
      .catch((e) => {
        throw new Error(e);
      });
  }, []);

  const handleDelete = () => {
    console.log(id);
    axios.delete(`http://localhost:8000/daily/${id}`);
    history.push("/daily");
    console.log("delete");
    console.log(detail);
  };

  return (
    <div>
      {loading ? (
        <h1>loading....</h1>
      ) : (
        <div>
          <h1>{detail.date}</h1>
          <h1>大学のこと</h1>
          <div
            dangerouslySetInnerHTML={{ __html: `${marked(`${detail.univ}`)}` }}
            className="detail-content"
          ></div>
          <h1>勉強</h1>
          <div
            dangerouslySetInnerHTML={{ __html: `${marked(`${detail.study}`)}` }}
            className="detail-content"
          ></div>
          <h1>その他</h1>
          <div
            dangerouslySetInnerHTML={{ __html: `${marked(`${detail.other}`)}` }}
            className="detail-content"
          ></div>
          <h1>初めましてだったこと</h1>
          <div
            dangerouslySetInnerHTML={{
              __html: `${marked(`${detail.first_meet}`)}`,
            }}
            className="detail-content"
          ></div>
          <h1>やりたいこと</h1>
          <div
            dangerouslySetInnerHTML={{
              __html: `${marked(`${detail.wanna_do}`)}`,
            }}
            className="detail-content"
          ></div>
          <h1>1日のまとめ</h1>
          <div
            dangerouslySetInnerHTML={{
              __html: `${marked(`${detail.summary}`)}`,
            }}
            className="detail-content"
          ></div>
        </div>
      )}
      {/* <CategoryList /> */}
      <form onSubmit={handleDelete}>
        <button type="danger" type="submit">
          Delete
        </button>
      </form>
    </div>
  );
};
